#define our Movie class that will hold movie objects
#each movie object will be created with 3 attributes
#movie title, poster_image_link and youtube_link
class Movie():
	def __init__(self, movie_title, poster_image_url, trailer_youtube_url):
		self.title = movie_title
		self.poster_image_url = poster_image_url
		self.trailer_youtube_url = trailer_youtube_url

	def create_html_video(self):
		youtube_link = self.trailer_youtube_url
		index = youtube_link.index("v=")+2
		youtube_id = youtube_link[index:]
		html_video = """
			<div class="col-md-3">
				<div class="lead">Title -%s-</div>
				<div>
					<img src="%s" data-toggle='modal' data-target='#%s' />
				</div>
			</div>
		<hr>

		<div class="modal" id="%s">
	      <div class="modal-dialog">
	        <div class="modal-content">
	          <a href="#" class="hanging-close" data-dismiss="modal" aria-hidden="true">
	            <img src="https://lh5.ggpht.com/v4-628SilF0HtHuHdu5EzxD7WRqOrrTIDi_MhEG6_qkNtUK5Wg7KPkofp_VJoF7RS2LhxwEFCO1ICHZlc-o_=s0#w=24&h=24"/>
	          </a>
	          <div class="scale-media" id="trailer-video-container">
	          	<iframe width="854" height="510" src="https://www.youtube.com/embed/%s" frameborder="0" allowfullscreen></iframe>
	          </div>
	        </div>
	      </div>
	    </div>
		""" % (
			self.title,
			self.poster_image_url,
			youtube_id,
			youtube_id,
			youtube_id
		)
		return html_video
	
#declare movie sample objects	
